      var bestDiff = 0xFFFFFF

      angular.forEach(photo.sizes, function (photoSize) {
        var diff = Math.abs(photoSize.w * photoSize.h - width * height)
        if (diff < bestDiff) {
          bestPhotoSize = photoSize
          bestDiff = diff
        }
      })

      // console.log('choosing', photo, width, height, bestPhotoSize)

