      var height = options.website ? 64 : Math.min(windowH - 100, Config.Mobile ? 210 : 260)
      var thumbPhotoSize = choosePhotoSize(photo, width, height)
      var thumb = {
        placeholder: 'img/placeholders/PhotoThumbConversation.gif',
        width: width,
        height: height
      }

