        if (popStickers && popStickers.length) {
          for (i = 0, len = popStickers.length; i < len; i++) {
            docID = popStickers[i][0]
            if (AppDocsManager.hasDoc(docID)) {
              result.push({id: docID, rate: popStickers[i][1]})
            }
          }
        }
        return result
